        "name":"sKumar",
        "class":"7A"
    }

    const [state,useState] = setState(state);

    return (
        // state will be available to all the chil
        <NoteContext.Provider value={state}> 
            {props.children}
        </NoteContext.Provider>
    )
}


export default NoteState;
