import React, { useContext, useEffect, useRef,useState } from 'react'
import noteContext from "../Context/notes/noteContext";
import AddNote from './AddNote';
import Noteitem from './Noteitem';



const Notes = () => {
    const context = useContext(noteContext);
    const { notes, getNotes } = context;
    const [note,setNote] = useState({etitle:"" ,edescription:"", etag:""});

    useEffect(() => {
        getNotes();
    }, []);

    const ref = useRef(null);

    const updateNote = (currentNode) => {
        ref.current.click();
        setNote({etitle: currentNode.title, edescription:currentNode.description,etag:currentNode.tag});
    }


    const handleClick=(e)=>{
        console.log("updating the note")
        e.preventDefault(); // page will not reload
    }

    const onChange=(event)=>{
        setNote({...note,[event.target.name]:event.target.value});
    }

    return (
        <>
            <AddNote />
            {/* <!-- Button trigger modal --> */}
            <button ref={ref} type="button" className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>

            {/* <!-- Modal --> */}
            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">Edit Note</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            {/* form will be populated same as a addnote */}
                            <form className="my-3">
                                <div className="mb-3">
                                    <label htmlFor="title" className="form-label">Title</label>
                                    <input type="text" className="form-control" id="etitle" name="etitle" value={note.etitle} aria-describedby="emailHelp" onChange={onChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="description" className="form-label">Descrption</label>
                                    <input type="text" className="form-control" id="edescription" name="edescription" value={note.edescription} onChange={onChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="tag" className="form-label">Tag</label>
                                    <input type="text" className="form-control" id="etag" name="etag" value={note.etag} onChange={onChange} />
                                </div>
                            </form>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" onClick={handleClick} className="btn btn-primary">Update Note</button>
                        </div>
                    </div>
                </div>
            </div>



            <div className="row my-3">
                <h2>Your Note</h2>
                {notes.map((note) => { // all notes title will be shown
                    // returning Noteitem by passing note as prpos and key as _id for a note 
                    return <Noteitem key={note._id} updateNote={updateNote} note={note} />
                })}
            </div>
        </>
    )
}

export default Notes;
