import NoteContext from "./noteContext";
import React, { useState } from "react";
import { json } from "react-router-dom";


const NoteState = (props)=>{
    // creates a state s1 which will be accessible to all the children 
    // const s1={
    //     "name":"sKumar",
    //     "class":"7A"
    // }

    // const [state,setState] = useState(s1);

    // const update=()=>{
    //     setTimeout(()=>{
    //         setState({
    //             "name" : "mrKumar",
    //             "class" : "10A"
    //         })
    //     },1500);
    // }

    const host = "http://localhost:4000"; // server address
    // sample notes for a particular user
    const notesInitial=[
        {
          "_id": "639c6bb374b02a293359bb43",
          "user": "639b63020ec8f22e7124944b",
          "title": "good morning notes",
          "description": "a note for a good morning",
          "tag": "personal",
          "date": "2022-12-16T12:59:31.176Z",
          "__v": 0
        },
        {
          "_id": "639c6bb374b02a293359bb45",
          "user": "639b63020ec8f22e7124944b",
          "title": "good morning notes",
          "description": "a note for a good morning",
          "tag": "personal",
          "date": "2022-12-16T12:59:31.336Z",
          "__v": 0
        },
        {
          "_id": "639e137fae1e66658ea29703",
          "user": "639b63020ec8f22e7124944b",
          "title": "good morning notes",
          "description": "a note for a good morning",
          "tag": "personal",
          "date": "2022-12-17T19:07:43.782Z",
          "__v": 0
        },
        {
          "_id": "639e1387ae1e66658ea29705",
          "user": "639b63020ec8f22e7124944b",
          "title": "good morning notes1",
          "description": "a note for a good morning",
          "tag": "personal",
          "date": "2022-12-17T19:07:51.136Z",
          "__v": 0
        },
        {
          "_id": "639e138bae1e66658ea29707",
          "user": "639b63020ec8f22e7124944b",
          "title": "good morning notes2",
          "description": "a note for a good morning",
          "tag": "personal",
          "date": "2022-12-17T19:07:55.194Z",
          "__v": 0
        },
        {
          "_id": "639e138eae1e66658ea29709",
          "user": "639b63020ec8f22e7124944b",
          "title": "good morning notes3",
          "description": "a note for a good morning",
          "tag": "personal",
          "date": "2022-12-17T19:07:58.833Z",
          "__v": 0
        }
      ];

      const[notes,setNote] = useState(notesInitial);

      // add a note
      const addNote=(title,description,tag)=>{
        // API call
        console.log("Adding a new note ");
        const note={
          "_id": "639c6bb374b02a293asasdasdas359bb43", // this key will always be unique
          "user": "639b63020ec8f22e7124944b",
          "title": title,
          "description": description,
          "tag": tag,
          "date": "2022-12-16T12:59:31.176Z",
          "__v": 0
        }
        setNote(notes.concat(note))
      }

      // delete a note
      const deleteNote=(id)=>{ // id is require to delete a note
        // API call 
          console.log("deleting a note with id: " + id);
          const newNote = notes.filter((note)=>{return note._id !== id});
          setNote(newNote);
      }

      // edit a note 
      const editNote=(id,title,description,tag)=>{ // id,title,description and tag is require to edit a note
          // API call
          const response =  fetch(url,{
            method:'POST',
            headers:{
              'Content-Type':'application/json'
            },
            body: JSON.stringify(data)
          });
          return response.json();

        for (let index = 0; index < notes.length; index++) {
          const element = notes[index];
          if(element._id === id){
            element.title = title;
            element.description = description;
            element.tag = tag;
          }
        }
      }

    return (
        // state will be available to all the chil
        // <NoteContext.Provider value={{state,update}}> 
        <NoteContext.Provider value={{notes,addNote,deleteNote,editNote}}>
            {props.children}
        </NoteContext.Provider>
    )
}


export default NoteState;
